let calendar=document.querySelector("#calendar"),submitButton=document.querySelector("#submit-button"),appending=document.querySelector(".appending"),personSex=document.querySelector("#sex");submitButton.addEventListener("click",e=>{e.preventDefault();let a=document.createElement("p");if(a.classList.add("appending__content"),appending.firstElementChild){let n=document.querySelector("#first-name").value,d=appending.querySelector("p"),l=new Date(calendar.value),t=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"],u=l.getDay(),r={Monday_Male:"Kwadwo",Tuesday_Male:"Kwabena",Wednesday_Male:"Kwaku",Thursday_Male:"Yaw",Friday_Male:"Kofi",Saturday_Male:"Kwame",Sunday_Male:"Kwasi",Monday_Female:"Adwoa",Tuesday_Female:"Abenaa",Wednesday_Female:"Akua",Thursday_Female:"Yaa",Friday_Female:"Afua",Saturday_Female:"Ama",Sunday_Female:"Akosua"},y=r[String(t[u])+"_"+String(personSex.value)];d.innerText=n+" "+y}else appending.appendChild(a),submitButton.click()});